//postOrder submit helper
export default function postOrder(newOrder, setIsLoading, resetForm, dispatch) {
  setIsLoading(true);
  axios
    .post(`/order`, newOrder)
    .then(result => {
      if (result.data) {
        messageAddOrder(result.data.name);
        dispatch(clearCart());
        resetForm();
      }
    })
    .catch(error => {
      messageError(error.response.data.message);
    })
    .finally(() => {
      setIsLoading(false);
    });
}
